mapper.select:
select count(*) from
USERINFO
where nickName = #{nickName}


SELECT *
FROM (
    SELECT ROWNUM AS rn, u.*
    FROM (
        SELECT UNO, USER_NAME, NICKNAME, USER_PW, USER_ID, PHONE, EMAIL, USER_STATUS, ACTIVE, POINT, BUSINESS_NO, LAST_LOGIN, REGDATE, USER_UPDATE, USER_UPDATE_UNO
        FROM USERINFO
        ORDER BY UNO DESC
    ) u
    WHERE ROWNUM <= #{endRow}
)
WHERE rn >= #{startRow}

SELECT user_id
FROM userinfo
WHERE user_name = #{name}
AND
email = #{email}

SELECT * FROM userinfo
WHERE user_id = #{userId}
AND email =
#{email}

mapper.update:
UPDATE userinfo
SET user_pw = #{userPw}
WHERE user_id =
#{userId}

UPDATE userinfo
SET user_loc = #{address}
WHERE user_id =
#{loginId}
